mapper.select:
select *
from m_sys_param
where
1=1
and param_name = #{paramName}
and param_value = #{paramValue}
and param_comment = #{paramComment}
and del_flg = #{delFlg}
and create_time = #{createTime}
and create_id = #{createId}
and update_time = #{updateTime}
and update_id = #{updateId}
ORDER BY ${orderByStr}

    limit ${limitCnt}
    offset ${offsetRowNum}

mapper.insert:
INSERT
INTO m_sys_param(
param_name
, param_value
, param_comment
, del_flg
, create_time
, create_id
, update_time
, update_id
)
VALUES (
#{paramName}
, #{paramValue}
, #{paramComment}
, #{delFlg}
, #{createTime}
, #{createId}
, #{updateTime}
, #{updateId}
)

mapper.update:
UPDATE m_sys_param
SET
param_name = #{paramName}
, param_value = #{paramValue}
, param_comment = #{paramComment}
, del_flg = #{delFlg}
, create_time = #{createTime}
, create_id = #{createId}
, update_time = #{updateTime}
, update_id = #{updateId}
WHERE
1=1
and param_name = #{paramName}
