mapper.select:
select *
from m_user
where
1=1
and id = #{id}
and user_name = #{userName}
and password = #{password}
and nick_name = #{nickName}
and wechat_open_id = #{wechatOpenId}
and custom_id = #{customId}
and user_type = #{userType}
and total_weight = #{totalWeight}
and user_lever = #{userLever}
and del_flg = #{delFlg}
and create_time = #{createTime}
and create_id = #{createId}
and update_time = #{updateTime}
and update_id = #{updateId}
ORDER BY ${orderByStr}

    limit ${limitCnt}
    offset ${offsetRowNum}

mapper.insert:
INSERT
INTO m_user(
user_name
, password
, nick_name
, wechat_open_id
, custom_id
, user_type
, total_weight
, user_lever
, del_flg
, create_id
, update_id
)
VALUES (
#{userName}
, #{password}
, #{nickName}
, #{wechatOpenId}
, #{customId}
, #{userType}
, #{totalWeight}
, #{userLever}
, #{delFlg}
, #{createId}
, #{updateId}
)

mapper.update:
UPDATE m_user
SET
id = #{id}
, user_name = #{userName}
, password = #{password}
, nick_name = #{nickName}
, wechat_open_id = #{wechatOpenId}
, custom_id = #{customId}
, user_type = #{userType}
, total_weight = #{totalWeight}
, user_lever = #{userLever}
, del_flg = #{delFlg}
, create_time = #{createTime}
, create_id = #{createId}
, update_time = #{updateTime}
, update_id = #{updateId}
WHERE
1=1
and id = #{id}
