mapper.select:
select *
from t_invoice_detail
where
1=1
and invoice_id = #{invoiceId}
and row_no = #{rowNo}
and lot_no = #{lotNo}
and tracking_no = #{trackingNo}
and weight = #{weight}
and status = #{status}
and del_flg = #{delFlg}
and create_time = #{createTime}
and create_id = #{createId}
and update_time = #{updateTime}
and update_id = #{updateId}
ORDER BY ${orderByStr}

    limit ${limitCnt}
    offset ${offsetRowNum}

mapper.insert:
INSERT
INTO t_invoice_detail(
invoice_id
, row_no
, lot_no
, tracking_no
, weight
, status
, del_flg
, create_time
, create_id
, update_time
, update_id
)
VALUES (
#{invoiceId}
, #{rowNo}
, #{lotNo}
, #{trackingNo}
, #{weight}
, #{status}
, #{delFlg}
, #{createTime}
, #{createId}
, #{updateTime}
, #{updateId}
)

mapper.update:
UPDATE t_invoice_detail
SET
invoice_id = #{invoiceId}
, row_no = #{rowNo}
, lot_no = #{lotNo}
, tracking_no = #{trackingNo}
, weight = #{weight}
, status = #{status}
, del_flg = #{delFlg}
, create_time = #{createTime}
, create_id = #{createId}
, update_time = #{updateTime}
, update_id = #{updateId}
WHERE
1=1
and invoice_id = #{invoiceId}
and row_no = #{rowNo}
