mapper.select:
select *
from t_invoice
where
1=1
and invoice_id = #{invoiceId}
and invoice_date = #{invoiceDate}
and invoice_time_cd = #{invoiceTimeCd}
and invoide_address = #{invoideAddress}
and weight = #{weight}
and custom_cd = #{customCd}
and name = #{name}
and tel_no = #{telNo}
and address = #{address}
and invoice_requirement = #{invoiceRequirement}
and invoice_status = #{invoiceStatus}
and total_weight = #{totalWeight}
and invoice_amount_jpy = #{invoiceAmountJpy}
and invoice_amount_cny = #{invoiceAmountCny}
and del_flg = #{delFlg}
and create_time = #{createTime}
and create_id = #{createId}
and update_time = #{updateTime}
and update_id = #{updateId}
ORDER BY ${orderByStr}

    limit ${limitCnt}
    offset ${offsetRowNum}

mapper.insert:
INSERT
INTO t_invoice(
invoice_date
, invoice_time_cd
, invoide_address
, weight
, custom_cd
, name
, tel_no
, address
, invoice_requirement
, invoice_status
, total_weight
, invoice_amount_jpy
, invoice_amount_cny
, del_flg
, create_time
, create_id
, update_time
, update_id
)
VALUES (
#{invoiceDate}
, #{invoiceTimeCd}
, #{invoideAddress}
, #{weight}
, #{customCd}
, #{name}
, #{telNo}
, #{address}
, #{invoiceRequirement}
, #{invoiceStatus}
, #{totalWeight}
, #{invoiceAmountJpy}
, #{invoiceAmountCny}
, #{delFlg}
, #{createTime}
, #{createId}
, #{updateTime}
, #{updateId}
)

mapper.update:
UPDATE t_invoice
SET
invoice_id = #{invoiceId}
, invoice_date = #{invoiceDate}
, invoice_time_cd = #{invoiceTimeCd}
, invoide_address = #{invoideAddress}
, weight = #{weight}
, custom_cd = #{customCd}
, name = #{name}
, tel_no = #{telNo}
, address = #{address}
, invoice_requirement = #{invoiceRequirement}
, invoice_status = #{invoiceStatus}
, total_weight = #{totalWeight}
, invoice_amount_jpy = #{invoiceAmountJpy}
, invoice_amount_cny = #{invoiceAmountCny}
, del_flg = #{delFlg}
, create_time = #{createTime}
, create_id = #{createId}
, update_time = #{updateTime}
, update_id = #{updateId}
WHERE
1=1
and invoice_id = #{invoiceId}

mapper.select:
SELECT A.* FROM `t_invoice` A INNER JOIN `m_user` B ON LEFT (A.custom_cd, 5) = B.custom_id WHERE B.id = #{createId}
AND A.invoice_id = #{invoiceId}
AND A.del_flg = '0' ORDER BY A.invoice_date DESC
