mapper.select:
select *
from t_shop_invoice
where
1=1
and shop_id = #{shopId}
and barcode = #{barcode}
and count = #{count}
and type = #{type}
and reason = #{reason}
and del_flg = #{delFlg}
and create_time = #{createTime}
and create_id = #{createId}
and update_time = #{updateTime}
and update_id = #{updateId}
ORDER BY ${orderByStr}

    limit ${limitCnt}
    offset ${offsetRowNum}

select a.barcode , b.item_name , b.price_rmb , b.price_show , sum(a.count) as sum_cnt from t_shop_invoice a left join m_shop_item b on a.shop_id = b.shop_id and a.barcode = b.barcode where a.shop_id = #{shopId} and a.type = '2'
and a.create_time >= #{dateFrom}
and a.create_time <= #{dateTo}
group by a.barcode, b.item_name, b.price_rmb, b.price_show

mapper.insert:
INSERT
INTO t_shop_invoice(
shop_id
, barcode
, count
, type
, reason
, del_flg
, create_time
, create_id
, update_time
, update_id
)
VALUES (
#{shopId}
, #{barcode}
, #{count}
, #{type}
, #{reason}
, #{delFlg}
, #{createTime}
, #{createId}
, #{updateTime}
, #{updateId}
)
